INSERT INTO `product` (`id`, `MaLoai`, `TenSanPham`, `MoTa`, `Gia`, `HinhAnh`, `HinhCT1`, `HinhCT2`, `HangSanXuat`, `Instock`, `created_at`, `updated_at`) VALUES
(1, 1, 'Điện Thoại iPhone X', 'iPhone X được Apple ra mắt ngày 12/9 vừa rồi đánh dấu chặng đường 10 năm lần đầu tiên iPhone ra đời. Sau một thời gian, giá iPhone X cũng được công bố. iPhone X mang trên mình thiết kế hoàn toàn mới với màn hình Super Retina viền cực mỏng và trang bị nhiều công nghệ hiện đại như nhận diện khuôn mặt Face ID, sạc pin nhanh và sạc không dây cùng khả năng chống nước bụi cao cấp.', 34790000, 'image/x.png', 'image/x_item1.png', 'image/x_item2.png', 'Apple', 0, '2020-08-18 04:49:54', '2020-08-20 05:31:01'),
(2, 1, 'Điện Thoại Vivo V9', 'Vivo V9 là chiếc smartphone tầm trung cận cao cấp với điểm nhấn là máy có camera kép phía sau và camera selfie độ phân giải cao 24 MP.Cũng giống như ZenFone 5, OPPO R15 hay Huawei P20, chiếc smartphone này của Vivo cũng dõi theo xu hướng thiết kế notch (hay còn gọi là tai thỏ) như trên chiếc iPhone X của Apple.sss', 7990000, 'image/v9.png', 'image/v9_item1.png', 'image/v9_item.jpg', 'Vivo', 2, '2020-08-18 04:49:54', '2020-09-10 07:05:48'),
(3, 1, 'Samsung Galaxy A6', 'Samsung Galaxy A6 (2018) là chiếc smartphone tầm trung vừa được ra mắt của Samsung bên cạnh chiếc Samsung Galaxy A6+ (2018).Thiết kế quen thuộcMáy được hoàn thiện từ kim loại nguyên khối, kết hợp với mặt kính cong nhẹ 2.5D cho cảm giác vuốt chạm thoải mái hơn.Mặt trước là camera \"tự sướng\" và đèn LED trợ sáng 3 cấp độ giúp chụp ảnh selfie trong điều kiện thiếu sáng tốt hơn.Mặt lưng là dãy ăng-ten trên dưới đối xứng hình chữ U và camera chính và cảm biến vân tay được gom chung vào 1 cụm.', 6990000, 'image/a6.png', 'image/a6_item1.png', 'image/a6_item2.png', 'Samsung', 34, '2020-08-18 04:49:54', '2020-09-10 06:53:53'),
(4, 1, 'Điện thoại OPPO A83s', 'OPPO A83 là chiếc smartphone dòng A đầu tiên sở hữu cho mình màn hình tràn viền và cùng với đó là camera với khả năng làm đẹp bằng AI nổi tiếng của OPPO.Thiết kế thon gọn Với xu thế màn hình 18:9 của năm 2017 thì máy sở hữu một thân hình \"thon thả\" với các góc cạnh được bo tròn mềm mại.', 4990000, 'image/a83.png', 'image/a83_item1.png', 'image/a83_item2.png', 'OPPO', 46, '2020-08-18 04:49:54', '2020-08-23 06:23:48'),
(5, 2, 'Chuột Bluetooth Apple', 'Phong cách thiết kế mang đậm bản sắc của Apple. Thời gian sử dụng chuột MLA02 kéo dài, chuột magic mouse MLA02 cho bạn thời gian sử dụng lên tới 1 tháng hoặc hơn chỉ với 1 lần sạc (2 tiếng). Cổng sạc cho chuột nằm ngay mặt dưới, dùng dây sạc đầu lightning (đi kèm với hộp).', 2490000, 'image/pk8.png', 'image/pk8_item1.jpg', 'image/pk8_item2.jpg', 'Apple', 3, '2020-08-18 04:49:54', '2020-09-01 06:11:54'),
(6, 2, 'Apple Watch S3 GPS', 'Về mặt tổng thể Apple Watch Series 3 có kích thước tương tự như Apple Watch Series 2. Sản phẩm chú trọng thêm về mặt thẩm mỹ với thiết kế sang trọng, năng động nhiều màu sắc khác nhau. Màn hình Apple Watch phiên bản 42 mm lớn 1.65 inch hiển thị chi tiết ảnh nhiều hơn, mặt đồng hồ cảm ứng trang bị mặt kính cường lực Sapphire crystal glass giúp bảo vệ mặt kính chống trầy xước tốt và hiển thị tốt dù là dưới ánh mặt trời.', 10990000, 'image/pk5.png', 'image/pk5_item1.jpg', '', 'Apple', 2, '2020-08-18 04:49:54', '2020-08-23 06:23:48'),
(7, 2, 'Ốp lưng iPhone X', 'Ốp lưng chính hãng Apple, nguyên seal 100%. Kiểu dáng thời trang và đẹp mắt, thiết kế vừa vặn và ôm sát thân máy,dễ dàng tháo/lắp ốp vào máy.', 1590000, 'image/pk4.png', 'image/pk4_item1.jpg', '', 'Apple', 0, '2020-08-18 04:49:54', '2020-08-18 04:49:54'),
(8, 2, 'Sạc dự phòng Polymer ', 'Pin sạc dự phòng Polymer Sony CP-S15-BC ULA tuy có dung lượng rất lớn nhưng kiểu dáng vẫn nhỏ gọn. Tích hợp công nghệ Hybrid Gel cho tuổi thọ sạc hơn 1000 lần. Dung lượng pin cao cho nhiều lần sạc,sạc đầy được cho điện thoại và máy tính bảng có dung lượng dưới 13.000 mAh. Sạc song song 2 thiết bị với tốc độ nhanh.', 1780000, 'image/pk1.png', 'image/pk1.png', 'image/pin1.png', 'Sony', 7, '2020-08-18 04:49:54', '2020-08-18 04:49:54'),
(9, 2, 'Sạc không dây 1A', 'Thiết kế đẹp mắt, tiện dụng, dễ dàng xếp lại và mang theo bất cứ nơi đâu. Tiện lợi chuyển đổi từ sạc đứng sang sạc nằm. Thưởng thức trọn vẹn cả khung hình, cho trải nghiệm giải trí tối đa ngay khi đang sạc. Chất liệu da cao cấp, giữ chắc điện thoại trên đế khi đang sạc. Sạc nhanh: tương thích với Galaxy Note8, S8, S8+, S7, S7 edge, Note5, S6 edge+ / Qi (Tương thích với Galaxy S6, S6 edge và các thiết bị được chứng nhận Qi).', 1450000, 'image/pk7.png', 'image/pk7.png', 'image/pk7_item1.jpg', 'Samsung', 8, '2020-08-18 04:49:54', '2020-08-18 04:49:54'),
(10, 2, 'Tai nghe Bluetooth', 'Airpods sở hữu thiết kế thời trang và nhỏ gọn, trọng lượng cũng rất nhẹ, mọi chi tiết đều được hoàn thiện, cải thiện thêm nhiều tính năng độc đáo. Nút tai nghe được làm rất vừa vặn với tai tạo cảm giác thoải mái, không bị khó chịu khi đeo trong thời gian dài nhưng cũng rất chắc chắn.', 4990000, 'image/pk2.png', 'image/pk2_item1.jpg', 'image/pk2_item2.jpg', 'Apple', 0, '2020-08-18 04:49:54', '2020-08-18 04:49:54'),
(11, 2, 'Micro SD 200GB', 'Thẻ nhớ dành cho các mẫu điện thoại cao cấp. Tương thích với thiết bị có thể nhận thẻ nhớ tối đa 200 GB. Chép một video dung lượng 10 GB vào thẻ chưa tới 2 phút. Phù hợp cho các dòng máy cơ, quay phim 4K.', 2500000, 'image/pk3.png', 'image/pk3-item1.jpg', 'image/pk3-item2.jpg', 'SD', 1, '2020-08-18 04:49:54', '2020-08-18 04:49:54'),
(12, 2, 'USB 3.0 Sandisk iXpand', 'Kết nối kép OTG USB-Lightning. USB 3.0 tốc độ cao. Bổ sung dung lượng lưu trữ tức thì cho iPhone, iPad. Bảo mật dữ liệu bằng hệ thống mã hoá. Chơi nhạc và video trực tiếp trên app SanDisk iXpand Drive. Tốc độ đọc tối đa: iPhone: 90MB/s, máy tính: 150MB/s.', 990000, 'image/pk6.png', 'image/pk6_item1.png', '', 'Apple', 2, '2020-08-18 04:49:54', '2020-08-18 04:49:54'),
(13, 3, 'Samsung Tab A6', 'Là dòng máy trong phân khúc tầm trung, Samsung Galaxy Tab A6 10.1 Spen được thiết kế với vỏ nhựa nhám nhẹ cùng các góc bo tròn cho cảm giác chắc chắn, không bám vân tay khi cầm. Sở hữu màn hình kích thước 10.1 inch không quá lớn, có thể nhét vừa một chiếc balo dạng nhỏ, Samsung Galaxy Tab A6 10.1 Spen sử dụng tấm nền PLS LCD cho khả năng hiển thị tốt. Độ phân giải  WUXGA 1920 x 1200 pixels cho chất lượng hình ảnh sắc nét, sử dụng tốt khi đi ngoài trời.', 7990000, 'image/tb1.png', 'image/tb1.png', 'image/tb1_item1.jpg', 'Samsung', 0, '2020-08-18 04:49:54', '2020-09-10 07:05:48'),
(14, 3, 'Máy tính bảng iPad Pro', 'Apple vẫn giữ ngôn ngữ thiết kế từ xa xưa tới nay, nên phiên bản 10.5 inch cũng không có gì khác lắm với những người tiền nhiệm còn lại. Tuy không mới nhưng đây vẫn là một thiết kế vượt thời gian và rất sang trọng.', 19990000, 'image/tb2.png', 'image/tb2.png', 'image/tb2_item1.jpg', 'Apple', 2, '2020-08-18 04:49:54', '2020-09-10 06:53:53'),
(15, 3, 'Huawei MediaPad', 'Về tổng thể, máy nổi bật với phần khung viền kim loại sang trọng, kết hợp màu sắc hiện đại cho cảm giác khá thanh lịch, nhẹ nhàng. Ngoài ra, với trọng lượng khá nhẹ cùng với 4 góc bo tròn giúp máy có trải nghiệm cầm nắm khá thoải mái. Sở hữu màn hình kích thước 10 inch không quá lớn, có thể nhét vừa một chiếc balo dạng nhỏ, Huawei MediaPad T3 10 (2017) Spen sử dụng tấm nền PLS LCD cho khả năng hiển thị tốt.', 4490000, 'image/tb3.png', 'image/tb3.png', 'image/tb3_item1.jpg', 'Huawei', 889, '2020-08-18 04:49:54', '2020-08-18 04:49:54'),
(16, 3, 'Lenovo Phab2', 'Lenovo Phab 2 lại có kích thước khá nặng cho người dùng cảm giác cầm nắm chắc chắn và cứng cáp. Máy được thiết kế từ nhôm, mặt lưng được bo cong về các cạnh cho máy ôm tay hơn khi sử dụng. Về phần màn hình khi máy sở hữu kích thước khá lớn 6.4 inch cùng độ phân giải 1280 x 720 pixels trên tấm nền IPS cho hình ảnh rõ nét.', 3990000, 'image/tb4.png', 'image/tb4.png', 'image/tb4_item1.jpg', 'Lenovo', 45, '2020-08-18 04:49:54', '2020-08-18 04:49:54'),
(17, 4, 'Apple Macbook Pro', 'Touch Bar sẽ thay thế các phím chức năng ở hàng đầu của bàn phím. Có thể xem đây là một màn hình thứ 2 của chiếc MacBook, nó hiển thị các hình ảnh với chất lượng cao hoặc các nút chức năng, công cụ tùy chỉnh tuỳ theo ứng dụng bạn đang dùng.', 44990000, 'image/lt1.png', 'image/lt1.png', 'image/lt1_item1.jpg', 'Apple', 7, '2020-08-18 04:49:54', '2020-08-18 04:49:54'),
(18, 4, 'Acer PREDATOR 21X', 'Acer PREDATOR 21 X là sản phẩm cực kỳ cao cấp và là laptop mạnh nhất thế giới ở thời điểm hiện tại với những trang thiết bị tối tân đặc cách cho \"Game Thủ\". Máy có cấu hình cực mạnh bao gồm CPU i7 thế hệ 7 7820HK, RAM 64 GB, ổ cứng 2 TB và đặc biệt là vũ khí hạng nặng 2 card đồ họa rời NVIDIA GeForce GTX 1080 với dung lượng 16 GB.', 229000000, 'image/lt2.png', 'image/lt2.png', 'image/lt2_item6.jpg', 'Acer', 34, '2020-08-18 04:49:54', '2020-09-10 06:53:53'),
(19, 4, 'Laptop Asus A541UA', 'Việc sử dụng chip Intel Core i3 6006U giúp máy hoạt động ổn định, khả năng xử lý tác vụ khá mượt mà. Kèm theo RAM DDR4 4 GB và ổ cứng HDD 1 TB rất phù hợp với sinh viên khi thường làm bài thuyết trình, người làm văn phòng hay nhập liệu và lưu dữ liệu thoải mái hơn.', 10790000, 'image/lt3.png', 'image/lt3.png', 'image/lt3_item1.png', 'Asus', 751, '2020-08-18 04:49:54', '2020-08-23 06:24:05'),
(20, 4, 'Laptop HP Pavilion 14', 'Mẫu laptop HP Core i3 sử dụng chip thế hệ thứ 7 mang lại hiệu năng hoạt động khá ổn định và có thể chơi các tựa game online khá ổn. Tiếp đó là RAM DDR4 4 GB hiệu quả và có hiệu suất hoạt động ổn định, cùng ổ cứng HDD 1 TB có thể lưu trữ nhiều dữ liệu cá nhân.', 12990000, 'image/lt4.png', 'image/lt4.png', 'image/lt4_item4.png', 'HP', 11, '2020-08-18 04:49:54', '2020-08-18 04:49:54'),
(38, 1, 'iPhone 6s Plus 32GB', 'Apple iPhone 6s Plus là chiếc iPhone màn hình lớn nhất, cho phép người dùng làm được nhiều việc hơn trên không gian rộng lớn. Ngoài ra, những tính năng mới như hệ điều hành iOS 10, 3D Touch, Live Photos hay camera 12MP xuất sắc sẽ mang đến sự hài lòng nhất cho bạn khi sử dụng một chiếc smartphone thực sự cao cấp.', 7990000, 'image/6s_d_1.jpg', 'image/6s_d_2.jpg', 'image/6s_d_3.jpg', 'Apple', 16, '2020-08-29 06:54:58', '2020-08-29 06:54:58'),
(39, 1, 'iPhone 7 Plus 32GB', 'iPhone 7 Plus dường như là sản phẩm được Apple chăm chút để vượt xa iPhone 7, trở thành sản phẩm rất đáng để nâng cấp so với iPhone 6s Plus khi được nâng cấp thêm camera kép cùng chức năng chụp chân dung xoá phông cực \"hot\". Không chỉ vậy, nâng cấp đáng giá khác như tốc độ xử lý nhanh hơn, chống nước, bụi, loa ngoài sống động... Tất cả đã tạo nên một sản phẩm hàng đầu tuyệt vời.', 8990000, 'image/7plus_d_1.jpg', 'image/7plus_d_2.jpg', 'image/7plus_d_3.jpg', 'Apple', 10, '2020-08-29 07:29:56', '2020-08-29 07:29:56'),
(40, 1, 'iPhone 8 Plus 64GB', 'Apple bất ngờ ra mắt bộ đôi iPhone 8 và iPhone 8 Plus tạo ra cơn bão mới càn quét hết các bảng xếp hạng siêu phẩm. Như thường lệ, iPhone 8 Plus từ thiết kế cho tới tính năng đều mang đến cho người dùng những trải nghiệm thú vị tuyệt vời không thể bỏ lỡ.', 13990000, 'image/8plus_d_1.jpg', 'image/8plus_d_2.jpg', 'image/8plus_d_3.jpg', 'Apple', 5, '2020-08-29 07:29:56', '2020-08-29 07:29:56'),
(41, 1, 'iPhone Xs Max 256GB', 'iPhone Xs Max 256GB có thể nói là chiếc smartphone có loa ngoài tốt bậc nhất hiện nay. Cụm loa kép stereo cho chất lượng khó tin về cả độ lớn lẫn độ chi tiết. Loa ngoài trên thực tế rất quan trọng khi nó thường xuyên được người dùng sử dụng, iPhone Xs Max mang đến những phút giây giải trí tuyệt vời với màn hình lớn đỉnh cao và loa ngoài cực chất.', 25990000, 'image/xs_d_1.jpg', 'image/xs_d_2.jpg', 'image/xs_d_3.jpg', 'Apple', 25, '2020-08-29 07:29:56', '2020-08-29 07:29:56'),
(42, 1, 'iPhone SE (2020) 64GB', 'Một chiếc iPhone nhỏ gọn mang trên mình bộ vi xử lý mạnh mẽ hàng đầu, iPhone SE 2020 với mức giá hấp dẫn chưa từng thấy sẽ là sự lựa chọn phù hợp dành cho tất cả người dùng.', 12490000, 'image/se2020_d_1.jpg', 'image/se2020_d_2.jpg', 'image/se2020_d_3.jpg', 'Apple', 66, '2020-08-29 07:29:56', '2020-08-29 07:29:56'),
(43, 1, 'iPhone 11 64GB', 'Sau bao nhiêu chờ đợi cũng như đồn đoán thì cuối cùng Apple đã chính thức giới thiệu bộ 3 siêu phẩm iPhone 11 mạnh mẽ nhất của mình vào tháng 9/2019. Có mức giá rẻ nhất nhưng vẫn được nâng cấp mạnh mẽ như chiếc iPhone Xr năm ngoái, đó chính là phiên bản iPhone 11 64GB.', 17990000, 'image/11_d_1.jpg', 'image/11_d_2.jpg', 'image/11_d_3.jpg', 'Apple', 105, '2020-08-29 07:29:56', '2020-08-29 07:29:56'),
(44, 1, 'iPhone 11 Pro 64GB', 'Hoàn thiện từ khung thép không gỉ chắc chắn và hai mặt kính cường lực bóng bẩy, iPhone 11 Pro 64GB thật sự nổi bật giữa đám đông. Chiếc iPhone nhỏ gọn, cho cảm giác cầm nắm hoàn hảo nhưng vẫn có màn hình đủ lớn để trải nghiệm tốt mọi thứ. 4 phiên bản màu sắc sang trọng, mang đến nhiều lựa chọn cho người dùng. 4 màu sắc iPhone 11 Pro bao gồm Vàng, Xám không gian, Bạc và đặc biệt là màu mới Xanh Midnight đầy cuốn hút.', 24990000, 'image/11pro_d_1.jpg', 'image/11pro_d_2.jpg', 'image/11pro_d_3.jpg', 'Apple', 51, '2020-08-29 07:29:56', '2020-08-29 07:29:56'),
(45, 1, 'iPhone 11 Pro Max 64GB', 'Được trang bị bộ vi xử lý Apple A13 Bionic, iPhone 11 Pro Max tự tin thể hiện đẳng cấp hiệu năng “Pro”. So với thế hệ trước, Apple A13 Bionic nhanh hơn 20% và tiêu thụ năng lượng ít hơn tới 40% cả về CPU lẫn GPU. iPhone 11 Pro Max mạnh mẽ vượt trội khi đặt cạnh bất cứ đối thủ nào trên thị trường hiện nay. Mọi tác vụ kể cả những tựa game nặng nhất cũng đều được thể hiện trơn tru, mượt mà trên iPhone 11 Pro Max 64GB.', 27990000, 'image/11promax_d_1.jpg', 'image/11promax_d_2.jpg', 'image/11promax_d_3.jpg', 'Apple', 66, '2020-08-29 07:29:56', '2020-08-29 07:29:56'),
(46, 1, 'Samsung Galaxy A21s', 'Chiếc điện thoại với những đột phá đầy ấn tượng, Samsung Galaxy A21s sở hữu 4 camera sau đa chức năng, camera trước nằm ngay trong tấm kính màn hình, mang đến trải nghiệm màn hình lớn hấp dẫn hơn bao giờ hết.', 4390000, 'image/46_d_1.png', 'image/46_d_2.png', 'image/46_d_3.png', 'Samsung', 0, '2020-09-03 03:47:54', '2020-09-03 04:29:19'),
(47, 1, 'Samsung Galaxy A11', 'Samsung Galaxy A11 có khả năng hỗ trợ chơi game, xem phim xuất sắc nhờ màn hình vô cực Infinity-O rộng 6.4 inch được thiết kế tràn viền mãn nhãn. Ngoài ra, chiếc điện thoại này còn đem lại trải nghiệm nhiếp ảnh chuyên nghiệp với cụm 3 camera ấn tượng cho góc chụp lên tới 115 độ.', 3290000, 'image/47_d_1.png', 'image/47_d_2.png', 'image/47_d_3.png', 'Samsung', 69, '2020-09-03 03:47:54', '2020-09-03 05:12:26'),
(48, 1, 'Samsung Galaxy A31', 'Sở hữu Samsung Galaxy A31, bạn sẽ có được trải nghiệm hình ảnh không thua kém smartphone cao cấp nào với màn hình rộng 6.4 inch rực rỡ sắc nét. Ngoài ra, khả năng chụp ảnh chuyên nghiệp nhờ cụm bốn camera trên mặt lưng cũng là điểm cộng không thể bỏ qua khi nhắc tới Galaxy A31.', 5890000, 'image/48_d_1.png', 'image/48_d_2.png', 'image/48_d_3.png', 'Samsung', 55, '2020-09-03 03:47:54', '2020-09-03 05:12:26'),
(49, 1, 'Samsung Galaxy Note 20 Ultra 5G', 'Đón chào kỷ nguyên mới cùng Samsung Galaxy Note20 Ultra 5G, nơi bạn sẽ được trải nghiệm tốc độ, sự thoải mái và đẳng cấp trên một chiếc điện thoại toàn năng, hội tụ của những công nghệ tinh túy nhất.', 32990000, 'image/49_d_1.png', 'image/49_d_2.png', 'image/49_d_3.png', 'Samsung', 35, '2020-09-03 03:47:54', '2020-09-03 05:12:26'),
(50, 1, 'Samsung Galaxy Note 20 Ultra', 'Chiếc Galaxy Note màn hình lớn nhất, bút S Pen thông minh nhất và những tính năng mạnh mẽ nhất đã xuất hiện. Samsung Galaxy Note20 Ultra sẽ định nghĩa lại smartphone, thay đổi những trải nghiệm của bạn theo cách thú vị hơn bao giờ hết.', 29990000, 'image/50_d_1.png', 'image/50_d_2.png', 'image/50_d_3.png', 'Samsung', 71, '2020-09-03 03:47:54', '2020-09-03 05:12:26'),
(51, 1, 'Samsung Galaxy Note 20', 'Siêu phẩm smartphone với sức mạnh như máy tính để bàn, đẹp tuyệt mỹ và bút S Pen ấn tượng nhất từ trước đến nay. Samsung Galaxy Note20 mang đến sự đột phá khác biệt, hoàn hảo trong mọi trải nghiệm.', 21990000, 'image/51_d_1.png', 'image/51_d_2.png', 'image/51_d_3.png', 'Samsung', 87, '2020-09-03 03:47:54', '2020-09-03 05:13:26'),
(52, 1, 'Samsung Galaxy M21', 'Đắm chìm trong màn hình tràn viền vô cực Infinity-U 6,4 inch rộng lớn, thỏa mãn niềm đam mê nhiếp ảnh với 3 camera sau ấn tượng, đặc biệt là viên pin dung lượng tới 6000 mAh cho 2 ngày sử dụng thoải mái, Samsung Galaxy M21 thực sự mang đến những giờ phút giải trí bất tận.', 5490000, 'image/52_d_1.png', 'image/52_d_2.png', 'image/52_d_3.png', 'Samsung', 80, '2020-09-03 03:47:54', '2020-09-03 05:13:26'),
(53, 1, 'Samsung Galaxy S20', 'Samsung S20 mang trên mình những tính năng cao cấp đầu bảng, với 3 camera 64MP, khả năng quay video 8K, sức mạnh phần cứng mạnh mẽ bên trong một thiết kế vô cùng di động, nhỏ gọn trong lòng bàn tay.', 16490000, 'image/53_d_1.png', 'image/53_d_2.png', 'image/53_d_3.png', 'Samsung', 73, '2020-09-03 03:47:54', '2020-09-03 05:13:26'),
(54, 1, 'Samsung Galaxy S20+', 'Siêu phẩm Samsung S20+ đã xuất hiện. Thể hiện đẳng cấp đích thực với thiết kế cao cấp, màn hình lớn đẹp tuyệt mỹ, 5 camera chuyên nghiệp và hiệu suất đáng kinh ngạc.', 16990000, 'image/54_d_1.png', 'image/54_d_2.png', 'image/54_d_3.png', 'Samsung', 72, '2020-09-03 03:47:54', '2020-09-03 05:13:26'),
(55, 1, 'Samsung Galaxy S20 Ultra', 'Samsung Galaxy S20 Ultra là chiếc điện thoại sẽ thay đổi tương lai của nhiếp ảnh smartphone. Cùng khám phá bước tiến mang tính lịch sử với 5 ống kính camera 108MP siêu nét, quay phim chuẩn điện ảnh 8K trên S20 Ultra.', 19990000, 'image/55_d_1.png', 'image/55_d_2.png', 'image/55_d_3.png', 'Samsung', 43, '2020-09-03 03:47:54', '2020-09-03 05:13:26'),
(56, 1, 'Samsung Galaxy Z Flip', 'Một chiếc điện thoại vừa nhỏ gọn đáng kinh ngạc, lại vừa mang đến trải nghiệm mãn nhãn trên màn hình lớn chuẩn điện ảnh 6,7 inch, những điều tưởng như nằm ngoài giới hạn đã thực sự xuất hiện trên Samsung Galaxy Z Flip với thiết kế gập độc đáo và sáng tạo chưa từng thấy.', 29990000, 'image/56_d_1.png', 'image/56_d_2.png', 'image/56_d_3.png', 'Samsung', 108, '2020-09-03 03:47:54', '2020-09-03 05:13:26'),
(57, 1, 'Samsung Galaxy M31', 'Dung lượng pin siêu khổng lồ 6000 mAh, 4 camera 64MP đẳng cấp và rất nhiều bất ngờ thú vị khác đang chờ đón bạn trên Samsung Galaxy M31, chiếc điện thoại giá tầm trung nhưng mang lại những trải nghiệm cao cấp.', 5690000, 'image/57_d_1.png', 'image/57_d_2.png', 'image/57_d_3.png', 'Samsung', 69, '2020-09-03 03:47:54', '2020-09-03 05:13:26'),
(58, 1, 'Samsung Galaxy Note 10 Lite', 'Chiếc Galaxy Note mới đã quay trở lại, Samsung Galaxy Note10 Lite đưa bạn đến trải nghiệm tuyệt vời của màn hình vô cực Infinity-O, bút S Pen danh tiếng, camera chuyên nghiệp và thời lượng pin dài bất tận trong một mức giá dễ chịu.', 9990000, 'image/58_d_1.png', 'image/58_d_2.png', 'image/58_d_3.png', 'Samsung', 190, '2020-09-03 03:47:54', '2020-09-03 05:13:26'),
(59, 1, 'Samsung Galaxy A71', 'Một sản phẩm thực sự đột phá, Samsung Galaxy A71 là tổng hòa của những công nghệ cao cấp nhất hiện nay với bộ 4 camera sau 64MP, màn hình 6,7 inch Full HD+ mãn nhãn và thời lượng pin trên cả tuyệt vời.', 8990000, 'image/59_d_1.png', 'image/59_d_2.png', 'image/59_d_3.png', 'Samsung', 169, '2020-09-03 03:47:54', '2020-09-03 05:13:26'),
(60, 1, 'Samsung Galaxy A51', 'Tự hào là smartphone đầu tiên trên thế giới được tích hợp camera Macro hỗ trợ chụp ảnh cận cảnh, Galaxy A51 đem tới trải nghiệm chụp hình vượt ngoài mong đợi với khả năng tái tạo đến từng chi tiết nhỏ nhất. Ngoài ra, sự ưu việt của màn hình vô cực kết hợp với cấu hình mạnh mẽ và viên pin lớn 4.000 mAh sạc siêu nhanh khiến Galaxy A51 có thể làm vừa lòng bất cứ ai ngay từ lần trải nghiệm đầu tiên.', 6990000, 'image/60_d_1.png', 'image/60_d_2.png', 'image/60_d_3.png', 'Samsung', 186, '2020-09-03 03:47:54', '2020-09-03 05:13:26'),
(61, 1, 'Samsung Galaxy Note 10+', 'Chiếc điện thoại cao cấp nhất, màn hình lớn nhất, mang trên mình sức mạnh đáng kinh ngạc của một chiếc máy tính và hệ thống 4 camera sau chuyên nghiệp, đó chính là Samsung Galaxy Note 10+, nơi quyền năng mới được thể hiện.', 26990000, 'image/61_d_1.png', 'image/61_d_2.png', 'image/61_d_3.png', 'Samsung', 150, '2020-09-03 03:47:54', '2020-09-03 05:13:26'),
(62, 1, 'Samsung Galaxy S10+ (128GB)', 'Chiếc điện thoại màn hình Infinity-O lớn nhất, camera xuất sắc nhất và hiệu năng mạnh mẽ nhất của Samsung đã xuất hiện. Galaxy S10+ dẫn đầu xu thế, mang trên mình các công nghệ tiên tiến của tương lai và là một tác phẩm nghệ thuật đích thực.', 18990000, 'image/62_d_1.png', 'image/62_d_2.png', 'image/62_d_3.png', 'Samsung', 185, '2020-09-03 03:47:54', '2020-09-03 05:13:26'),
(63, 1, 'Samsung Galaxy A50s', 'Giải trí, chụp hình và chia sẻ, Samsung Galaxy A50s mới sẽ đáp ứng mọi nhu cầu của bạn trong cuộc sống hiện đại bằng hiệu năng tuyệt vời, 3 camera sau đẳng cấp, cùng thời lượng pin dài bất tận, thỏa sức khám phá.', 4990000, 'image/63_d_1.png', 'image/63_d_2.png', 'image/63_d_3.png', 'Samsung', 124, '2020-09-03 03:47:54', '2020-09-03 05:13:26'),
(64, 1, 'Samsung Galaxy S10e', 'Samsung Galaxy S10e là chiếc điện thoại nhỏ gọn hoàn hảo với hiệu năng mạnh mẽ nhất, camera thay đổi khẩu độ xuất sắc và màn hình Infinity-O tuyệt mỹ.', 10990000, 'image/64_d_1.png', 'image/64_d_2.png', 'image/64_d_3.png', 'Samsung', 115, '2020-09-03 03:47:54', '2020-09-03 05:13:26'),
(65, 1, 'Samsung Galaxy S10', 'Tuyệt tác của thiết kế với màn hình Infinity-O độc đáo lần đầu tiên đã xuất hiện. Không đơn thuần là một chiếc điện thoại, Samsung S10 còn là đỉnh cao của công nghệ.', 20990000, 'image/65_d_1.png', 'image/65_d_2.png', 'image/65_d_3.png', 'Samsung', 65, '2020-09-03 03:47:54', '2020-09-03 05:13:26'),
(66, 1, 'Samsung Galaxy A30S', 'Với 3 camera sau chuyên nghiệp, màn hình Super AMOLED đẳng cấp và âm thanh Dolby Atmos tuyệt hảo, Samsung Galaxy A30s đưa người dùng đến thế giới giải trí sống động chưa từng thấy.', 4290000, 'image/66_d_1.png', 'image/66_d_2.png', 'image/66_d_3.png', 'Samsung', 179, '2020-09-03 03:47:54', '2020-09-03 05:13:26'),
(67, 1, 'Samsung Galaxy A80', 'Samsung A80 là chiếc điện thoại dẫn đầu kỷ nguyên mới với màn hình vô cực hoàn hảo, camera trượt xoay 180 độ độc đáo, cảm biến vân tay trong màn hình và những đột phá đầy ấn tượng.', 8990000, 'image/67_d_1.png', 'image/67_d_2.png', 'image/67_d_3.png', 'Samsung', 170, '2020-09-03 03:47:54', '2020-09-03 05:13:26'),
(68, 1, 'Samsung Galaxy A70', 'Chiếc điện thoại dành cho những điều lớn: màn hình lớn, viên pin dung lượng cao; 3 camera độ phân giải “siêu khủng”, Samsung A70 mở ra kỷ nguyên mới cho riêng bạn.\r\n\r\n', 6290000, 'image/68_d_1.png', 'image/68_d_2.png', 'image/68_d_3.png', 'Samsung', 170, '2020-09-03 03:47:54', '2020-09-03 05:13:26'),
(69, 1, 'Samsung Galaxy A20', 'Siêu phẩm di động hấp dẫn nhất phân khúc tầm trung đã xuất hiện, Samsung A20 là sản phẩm hội tụ đầy đủ mọi ưu điểm trong mơ của một chiếc smartphone với mức giá hấp dẫn, màn hình lớn hơn bao giờ hết, dung lượng pin “khủng” và khả năng chụp ảnh tốt.', 3540000, 'image/69_d_1.png', 'image/69_d_2.png', 'image/69_d_3.png', 'Samsung', 52, '2020-09-03 03:47:54', '2020-09-03 05:13:26'),
(70, 1, 'Samsung Galaxy A10s', 'Chiếc điện thoại màn hình lớn tràn viền cực đã, thời lượng pin xuất sắc, camera kép xóa phông chuyên nghiệp trong tầm giá hấp dẫn, Samsung Galaxy A10s đã sẵn sàng cùng bạn khám phá cuộc sống năng động, tràn đầy năng lượng.', 3140000, 'image/70_d_1.png', 'image/70_d_2.png', 'image/70_d_3.png', 'Samsung', 56, '2020-09-03 03:47:54', '2020-09-03 05:13:26'),
(71, 1, 'Samsung Galaxy A01 Core', 'Chiếc điện thoại nhỏ gọn thời trang đến từ thương hiệu danh tiếng hàng đầu thế giới, Samsung Galaxy A01 Core mang đến những trải nghiệm thông minh và độ bền đáng tin cậy trong tầm giá rẻ hấp dẫn.\r\n\r\n', 1990000, 'image/71_d_1.png', 'image/71_d_2.png', 'image/71_d_3.png', 'Samsung', 179, '2020-09-03 03:47:54', '2020-09-03 05:13:26'),
(72, 1, 'Samsung Galaxy Note 10', 'Quyền năng thế hệ mới đã xuất hiện, Samsung Galaxy Note 10 mang đến trải nghiệm tuyệt đỉnh như máy tính trong một thiết kế gọn gàng cùng bút S Pen thông minh, tất cả đều nằm trên tay bạn.', 22990000, 'image/72_d_1.png', 'image/72_d_2.png', 'image/72_d_3.png', 'Samsung', 189, '2020-09-03 03:47:55', '2020-09-03 05:13:26'),
(73, 1, 'OPPO Reno 4', 'OPPO Reno4 với cấu hình mạnh mẽ và công nghệ sạc siêu nhanh sẽ giúp bạn có được hiệu suất cao để trải nghiệm những điều thú vị trong cuộc sống, nhất là trên bộ tứ camera đẳng cấp cùng thiết kế mà ai cũng phải ngước nhìn.', 8490000, 'image/73_d_1.png', 'image/73_d_2.png', 'image/73_d_3.png', 'OPPO', 16, '2020-09-08 03:28:21', '2020-09-09 05:42:38'),
(74, 1, 'OPPO Reno 4 Pro', 'OPPO Reno4 Pro sở hữu những công nghệ hàng đầu của smartphone hiện nay. Từ 4 camera xuất sắc, hiệu năng mạnh mẽ cho đến tốc độ sạc siêu nhanh, tất cả góp phần tạo nên một chiếc điện thoại đẳng cấp.', 11990000, 'image/74_d_1.png', 'image/74_d_2.png', 'image/74_d_3.png', 'OPPO', 69, '2020-09-08 03:28:21', '2020-09-09 05:42:38'),
(75, 1, 'OPPO A92', 'Màn hình chấm O tinh tế, hiệu năng mạnh mẽ, 4 camera AI 48MP và viên pin dung lượng cực “khủng”, bạn sẽ hoàn toàn hài lòng về OPPO A92, chiếc điện thoại siêu mượt mà, đầy đẳng cấp.', 6690000, 'image/75_d_1.png', 'image/75_d_2.png', 'image/75_d_3.png', 'OPPO', 108, '2020-09-08 03:34:50', '2020-09-09 05:42:38'),
(76, 1, 'OPPO A53 4GB-128GB', 'Mượt mà hơn bao giờ hết với màn hình 90Hz, sạc nhanh siêu tốc 18W và camera 16MP selfie tuyệt đẹp, OPPO A53 mang đến cho người dùng những bất ngờ thú vị trong tầm giá hấp dẫn.', 4490000, 'image/76_d_1.png', 'image/76_d_2.png', 'image/76_d_3.png', 'OPPO', 24, '2020-09-08 03:34:50', '2020-09-09 05:42:38'),
(77, 1, 'OPPO Reno3 Pro 8GB-256GB', 'Bạn đang cần một chiếc điện thoại chụp ảnh thực sự chuyên nghiệp ở cả camera trước và sau, hãy đến với OPPO Reno3 Pro, chiếc “camera phone” đẳng cấp, sẽ cùng bạn khai phá những khoảnh khắc vàng trong mọi bối cảnh.', 10490000, 'image/77_d_1.png', 'image/77_d_2.png', 'image/77_d_3.png', 'OPPO', 47, '2020-09-08 03:34:50', '2020-09-09 05:42:38'),
(78, 1, 'OPPO Reno3 8GB-128GB', 'Chiếc “camera phone” thế hệ mới nhất đã xuất hiện, OPPO Reno3 sở hữu 4 camera sau đẳng cấp và camera chuyên selfie đêm 44MP cực chất sẽ nâng tầm trải nghiệm nhiếp ảnh của bạn, mang đến sự sáng tạo đầy thú vị.', 6990000, 'image/78_d_1.png', 'image/78_d_2.png', 'image/78_d_3.png', 'OPPO', 34, '2020-09-08 03:34:50', '2020-09-09 05:42:38'),
(79, 1, 'OPPO Reno2 F', 'Chụp ảnh đỉnh cao với 4 camera sau; thiết kế không viền tuyệt mỹ; cảm biến vân tay trong màn hình và camera trước “tàng hình” độc đáo, OPPO Reno2 F tập hợp tất cả những tinh hoa công nghệ trong một sản phẩm tuyệt đẹp.', 6490000, 'image/79_d_1.png', 'image/79_d_2.png', 'image/79_d_3.png', 'OPPO', 105, '2020-09-08 03:34:50', '2020-09-09 05:42:38'),
(81, 1, 'OPPO A9 2020', 'OPPO A9 2020 tập trung tối đa vào trải nghiệm người dùng với hiệu năng và thời lượng pin cực đỉnh. Chắc chắn bạn sẽ phải bất ngờ vì những gì OPPO A9 mang lại.', 5490000, 'image/81_d_1.png', 'image/81_d_2.png', 'image/81_d_3.png', 'OPPO', 117, '2020-09-08 03:34:50', '2020-09-09 05:42:38'),
(82, 1, 'OPPO A31 6GB-128GB', 'Phiên bản OPPO A31 6GB không chỉ có sẵn nhiều bộ nhớ trong mà dung lượng RAM cũng được nâng cấp, để bạn có được trải nghiệm mượt mà hơn, thỏa sức giải trí trên màn hình lớn 6,5 inch tuyệt đẹp.', 4790000, 'image/82_d_1.png', 'image/82_d_2.png', 'image/82_d_3.png', 'OPPO', 54, '2020-09-08 03:34:50', '2020-09-09 05:42:38'),
(83, 1, 'OPPO A31 4GB-128GB', 'Với bộ nhớ trong cực lớn 128GB, viên pin dung lượng cao 4230 mAh và màn hình giọt nước 6,5 inch, OPPO A31 mang đến cho bạn những trải nghiệm lớn hơn, giúp cuộc sống trở nên dễ dàng hơn.', 4190000, 'image/83_d_1.png', 'image/83_d_2.png', 'image/83_d_3.png', 'OPPO', 97, '2020-09-08 03:40:30', '2020-09-09 05:42:38'),
(84, 1, 'OPPO A12 4GB-64GB', 'OPPO A12 4GB – 64GB mang đến cho bạn một phiên bản bộ nhớ nhiều hơn, chạy mượt hơn, bên cạnh những ưu điểm từ thiết kế độc đáo, màn hình “giọt nước” thời thượng và cấu hình xuất sắc trong tầm giá.', 3690000, 'image/84_d_1.png', 'image/84_d_2.png', 'image/84_d_3.png', 'OPPO', 90, '2020-09-08 03:40:30', '2020-09-09 05:42:38'),
(85, 1, 'OPPO A12 3GB-32GB', 'Thể hiện cá tính của bạn bằng thiết kế kim cương 3D độc đáo của OPPO A12, cùng với màn hình lớn “giọt nước” tuyệt mỹ và camera kép sành điệu, bạn sẽ luôn vui vẻ năng động suốt cả ngày.', 2990000, 'image/85_d_1.png', 'image/85_d_2.png', 'image/85_d_3.png', 'OPPO', 14, '2020-09-08 03:40:30', '2020-09-09 05:42:38'),
(86, 1, 'OPPO A1K', 'Chiếc điện thoại thời trang đầy phong cách nhưng lại sở hữu thời lượng pin đáng nể, OPPO A1K mang đến nguồn năng lượng bất tận cho giới trẻ, để bạn thoải mái sử dụng trong suốt cả ngày dài.', 2690000, 'image/86_d_1.png', 'image/86_d_2.png', 'image/86_d_3.png', 'OPPO', 51, '2020-09-08 03:40:30', '2020-09-09 05:42:38'),
(87, 1, 'OPPO Find X2', 'Tiếp tục sứ mệnh mở ra tương lai bằng những công nghệ mới, OPPO Find X2 mang đến cho bạn trải nghiệm chưa từng có, để bạn được cảm nhận sự đẳng cấp và những bước tiến vượt bậc trong ngành smartphone.', 19990000, 'image/87_d_1.png', 'image/87_d_2.png', 'image/87_d_3.png', 'OPPO', 40, '2020-09-08 03:40:30', '2020-09-09 05:42:38'),
(88, 1, 'Xiaomi Mi Note 10 Lite 8GB-128GB', 'Là một trong những smartphone đáng chú ý nhất của Xiaomi trên thị trường hiện nay, Mi Note 10 Lite đem tới trải nghiệm sử dụng cực kì cao cấp với cụm bốn camera hiện đại, chip xử lý Snapdragon 730 mạnh mẽ và màn hình hiển thị sắc nét được vuốt cong điệu đà về hai phía.', 9490000, 'image/88_d_1.png', 'image/88_d_2.png', 'image/88_d_3.png', 'Xiaomi', 16, '2020-09-11 05:12:52', '2020-09-11 05:12:52'),
(89, 1, 'Xiaomi Redmi Note 9 Pro 6GB-128GB', 'Siêu phẩm mới nhất từ dòng máy bán chạy hàng đầu trong những năm qua, Redmi Note 9 Pro với sức mạnh hiệu năng tuyệt đỉnh, 4 camera sau 64MP đẳng cấp, sẽ cùng bạn chinh phục mọi giới hạn.', 6990000, 'image/89_d_1.png', 'image/89_d_2.png', 'image/89_d_3.png', 'Xiaomi', 162, '2020-09-11 05:32:45', '2020-09-11 05:32:45'),
(90, 1, 'Xiaomi Redmi Note 9 Pro 6GB-64GB', 'Redmi Note 9 Pro phiên bản màu xanh cực quang trẻ trung, sở hữu cấu hình mạnh mẽ vượt trội trong tầm giá và 4 camera 64MP chất lượng đáng kinh ngạc, mang đến cho bạn chiếc điện thoại đẳng cấp flagship đích thực.', 6490000, 'image/90_d_1.png', 'image/90_d_2.png', 'image/90_d_3.png', 'Xiaomi', 137, '2020-09-11 05:32:45', '2020-09-11 05:32:45'),
(91, 1, 'Xiaomi Redmi Note 8 Pro 6GB-128GB', 'Phiên bản Xiaomi Redmi Note 8 Pro 6GB-128GB mang trên mình sức mạnh hiệu năng đáng kinh ngạc cùng khả năng chụp ảnh đẳng cấp với 4 camera 64MP hàng đầu.', 5440000, 'image/91_d_1.png', 'image/91_d_2.png', 'image/91_d_3.png', 'Xiaomi', 136, '2020-09-11 05:32:45', '2020-09-11 05:32:45'),
(92, 1, 'Xiaomi Redmi Note 9 4GB-128GB', 'Xiaomi Redmi Note 9 viết tiếp câu chuyện thành công của Redmi series, dòng điện thoại bán chạy nhất từ trước đến nay của Xiaomi. Với sức mạnh đáng kinh ngạc, thời lượng pin vượt trội và 4 camera AI chuyên nghiệp, Redmi Note 9 sẽ mang đến những trải nghiệm chưa từng có cho người dùng smartphone giá rẻ.', 4790000, 'image/92_d_1.png', 'image/92_d_2.png', 'image/92_d_3.png', 'Xiaomi', 138, '2020-09-11 05:32:45', '2020-09-11 05:32:45'),
(93, 1, 'Xiaomi Redmi Note 8 4GB-64GB', 'Chiếc điện thoại với 4 camera sau Redmi Note 8 mang đến cho bạn khả năng chụp ảnh tuyệt vời. Đồng thời hiệu năng và thời lượng pin cực đỉnh đúng như truyền thống từ trước đến nay của Xiaomi.', 4490000, 'image/93_d_1.png', 'image/93_d_2.png', 'image/93_d_3.png', 'Xiaomi', 199, '2020-09-11 05:32:45', '2020-09-11 05:32:45'),
(94, 1, 'Xiaomi Redmi 9 3GB-32GB', 'Xiaomi Redmi Note 9 viết tiếp câu chuyện thành công của Redmi series, dòng điện thoại bán chạy nhất từ trước đến nay của Xiaomi. Với sức mạnh đáng kinh ngạc, thời lượng pin vượt trội và 4 camera AI chuyên nghiệp, Redmi Note 9 sẽ mang đến những trải nghiệm chưa từng có cho người dùng smartphone giá rẻ.', 3590000, 'image/94_d_1.png', 'image/94_d_2.png', 'image/94_d_3.png', 'Xiaomi', 104, '2020-09-11 05:32:45', '2020-09-11 05:32:45'),
(95, 1, 'Xiaomi Redmi 9 3GB-32GB', 'Màn hình lớn 6,53 inch Full HD sắc nét, 4 camera AI thông minh, cấu hình mạnh mẽ và thời lượng pin cực dài, Redmi 9 mang đến cho bạn trải nghiệm đầy đủ của một chiếc smartphone giá rẻ, nhưng tính năng không hề rẻ.', 2690000, 'image/95_d_1.png', 'image/95_d_2.png', 'image/95_d_3.png', 'Xiaomi', 165, '2020-09-11 05:32:45', '2020-09-11 05:32:45'),
(96, 1, 'Xiaomi Redmi 9C 3GB-64GB', 'Phiên bản Xiaomi Redmi 9C 64GB được nâng cấp cả về dung lượng RAM và bộ nhớ trong, mang đến cho bạn trải nghiệm mượt mà hơn, trong một thiết kế hiện đại, trẻ trung cùng thời lượng pin cực đỉnh.', 2990000, 'image/96_d_1.png', 'image/96_d_2.png', 'image/96_d_3.png', 'Xiaomi', 145, '2020-09-11 05:32:45', '2020-09-11 05:32:45'),
(97, 1, 'Xiaomi Redmi 9C 2GB-32GB', 'Xiaomi Redmi 9C với thiết kế đầy tươi trẻ cùng hiệu năng tuyệt vời trong tầm giá, sẽ thổi một làn gió mới cho cuộc sống của bạn luôn tràn đầy năng lượng.', 2490000, 'image/97_d_1.png', 'image/97_d_2.png', 'image/97_d_3.png', 'Xiaomi', 148, '2020-09-11 05:32:45', '2020-09-11 05:32:45'),
(98, 1, 'Xiaomi Redmi 9A 2GB-32GB', 'Xiaomi Redmi 9A thể hiện sự vượt trội trong phân khúc giá rẻ khi sở hữu màn hình lớn, thời lượng pin suốt cả ngày và cấu hình tuyệt đỉnh, mang đến cho bạn trải nghiệm thực sự hài lòng.', 1990000, 'image/98_d_1.png', 'image/98_d_2.png', 'image/98_d_3.png', 'Xiaomi', 105, '2020-09-11 05:32:45', '2020-09-11 05:32:45'),
(99, 1, 'Poco X3 NFC 6GB-128GB', NULL, NULL, 'image/99_d_1.png', 'image/99_d_2.png', 'image/99_d_3.png', 'Xiaomi', 171, '2020-09-11 05:32:45', '2020-09-11 05:32:45');
